import os
import signal
import time

import pytest
from bjoern.tests.conftest import _run_app


@pytest.fixture()
def wsgi_headers_app():
    def app(env, start_response):
        start_response(
            "200 yo", [("Content-Type", "text/plain"), ("a" * 1000, "b" * 1000)]
        )
        return [b"foo", b"bar"]

    p = _run_app(app)
    try:
        yield p
    finally:
        os.kill(p.pid, signal.SIGKILL)
        time.sleep(1)  # Should be enough for the server to stop


def test_wsgi_headers_app(wsgi_headers_app, client):
    response = client.get("/")
    assert response.status_code == 200
    assert response.reason == "yo"
    assert response.headers == {
        "Content-Type": "text/plain",
        "": "",
        "Connection": "Keep-Alive",
        "Transfer-Encoding": "chunked",
    }
